(ns adventofcode.day06-test
  (:require [adventofcode.day06 :refer [day06 strategy1 strategy2]]
            [clojure.test :refer :all]))

(def test-input "")

(deftest day06-test
  (testing "part 1"
    (is (= 7 (day06 strategy1 "mjqjpqmgbljsphdztnvjfqwrcgsmlb")))
    (is (= 5 (day06 strategy1 "bvwbjplbgvbhsrlpgdmjqwftvncz")))
    (is (= 6 (day06 strategy1 "nppdvjthqldpwncqszvftbrmjlhg")))
    (is (= 10 (day06 strategy1 "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg")))
    (is (= 11 (day06 strategy1 "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw")))
    (is (= 1909 (day06 strategy1 test-input))))
  (testing "part 2"
    (is (= 19 (day06 strategy2 "mjqjpqmgbljsphdztnvjfqwrcgsmlb")))
    (is (= 23 (day06 strategy2 "bvwbjplbgvbhsrlpgdmjqwftvncz")))
    (is (= 23 (day06 strategy2 "nppdvjthqldpwncqszvftbrmjlhg")))
    (is (= 29 (day06 strategy2 "nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg")))
    (is (= 26 (day06 strategy2 "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw")))
    (is (= 3380 (day06 strategy2 test-input)))))
